#include "ui.h"

static void temp_slider_update_label(int position)
{
    if (!ui_TempIn) {
        return;
    }
    if (position <= 0) {
        lv_label_set_text(ui_TempIn, "LO");
        return;
    }
    if (position >= UI_TEMP_ARC_STEPS) {
        lv_label_set_text(ui_TempIn, "HI");
        return;
    }

    int temp_tenths = UI_TEMP_MIN_TENTHS + position * UI_TEMP_STEP_TENTHS;
    char buf[8];
    if (temp_tenths % 10 == 0) {
        lv_snprintf(buf, sizeof(buf), "%d째", temp_tenths / 10);
    } else {
        lv_snprintf(buf, sizeof(buf), "%d.%d째", temp_tenths / 10, temp_tenths % 10);
    }
    lv_label_set_text(ui_TempIn, buf);
}

static void temp_slider_event_cb(lv_event_t * e)
{
    if (lv_event_get_code(e) != LV_EVENT_VALUE_CHANGED) {
        return;
    }
    lv_obj_t * slider = lv_event_get_target(e);
    temp_slider_update_label(lv_arc_get_value(slider));
}

